example.code:
//
// Lambda style
//

// listening
notification = MPMusicPlayerController.Notifications.ObservePlaybackStateDidChange ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);
});

// To stop listening:
notification.Dispose ();

//
// Method style
//
NSObject notification;
void Callback (object sender, Foundation.NSNotificationEventArgs args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);
}

void Setup ()
{
    notification = MPMusicPlayerController.Notifications.ObservePlaybackStateDidChange (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}

// Lambda style
NSNotificationCenter.DefaultCenter.AddObserver (
        MPMusicPlayerController.PlaybackStateDidChangeNotification, (notification) => {Console.WriteLine ("Received the notification MPMusicPlayerController", notification); }


// Method style
void Callback (NSNotification notification)
{
    Console.WriteLine ("Received a notification MPMusicPlayerController", notification);
}

void Setup ()
{
    NSNotificationCenter.DefaultCenter.AddObserver (MPMusicPlayerController.PlaybackStateDidChangeNotification, Callback);
}

