example.code:
//
// Lambda style
//

// listening
notification = UITextInputMode.Notifications.ObserveCurrentInputModeDidChange ((sender, args) => {
    /* Access strongly typed args */
    Console.WriteLine ("Notification: {0}", args.Notification);
});

// To stop listening:
notification.Dispose ();

//
// Method style
//
NSObject notification;
void Callback (object sender, Foundation.NSNotificationEventArgs args)
{
    // Access strongly typed args
    Console.WriteLine ("Notification: {0}", args.Notification);
}

void Setup ()
{
    notification = UITextInputMode.Notifications.ObserveCurrentInputModeDidChange (Callback);
}

void Teardown ()
{
    notification.Dispose ();
}

// Lambda style
NSNotificationCenter.DefaultCenter.AddObserver (
        UITextInputMode.CurrentInputModeDidChangeNotification, (notification) => {Console.WriteLine ("Received the notification UITextInputMode", notification); }


// Method style
void Callback (NSNotification notification)
{
    Console.WriteLine ("Received a notification UITextInputMode", notification);
}

void Setup ()
{
    NSNotificationCenter.DefaultCenter.AddObserver (UITextInputMode.CurrentInputModeDidChangeNotification, Callback);
}
